        return new Source("{% extends 'base.html.twig' %}

{% block title %}Snow Trick / Log in!{% endblock %}

{% block header %}{% endblock %}

{% block content %}
    <div class=\"container mt-5\">
        <div class=\"row mt-5\">
            <div class=\"card mt-5  ml-auto mr-auto\">
                <div class=\"col-md-12  mb-2 \">

                    <form method=\"post\">
                        {% if error %}
                            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                        {% endif %}

                        <h1 class=\"h3 mt-3 mb-4 font-weight-normal\">Se connnecter</h1>
                        <label for=\"inputEmail\" class=\"sr-only\">Email</label>
                        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\"
                               class=\"form-control mb-3\"
                               placeholder=\"Email\" required autofocus>
                        <label for=\"inputPassword\" class=\"sr-only \">Password</label>
                        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control mb-3\"
                               placeholder=\"Password\"
                               required>

                        <input type=\"hidden\" name=\"_csrf_token\"
                               value=\"{{ csrf_token('authenticate') }}\"
                        >

                        {#
                            Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
                            See https://symfony.com/doc/current/security/remember_me.html

                            <div class=\"checkbox mb-3\">
                                <label>
                                    <input type=\"checkbox\" name=\"_remember_me\"> Remember me
                                </label>
                            </div>
                        #}

                        <button class=\"btn btn-lg btn-primary mb-3\" type=\"submit\">
                            Se connecter
                        </button>
                        <a href=\"{{ path('forgot_password') }}\">Mot de passe oubli√©</a>
                    </form>
                </div>
            </div>
        </div>
    </div>
{% endblock %}
